import axios from 'axios';

const SIGNUP_REQUEST = 'react-redux-gallery/auth/SIGNUP_REQUEST';
const SIGNUP_SUCCESS = 'react-redux-gallery/auth/SIGNUP_SUCCESS';
const SIGNUP_FAILURE = 'react-redux-gallery/auth/SIGNUP_FAILURE';

const LOGIN_REQUEST = 'react-redux-gallery/auth/LOGIN_REQUEST';
const LOGIN_SUCCESS = 'react-redux-gallery/auth/LOGIN_SUCCESS';
const LOGIN_FAILURE = 'react-redux-gallery/auth/LOGIN_FAILURE';

const GET_USER_DATA_REQUEST = 'react-redux-gallery/auth/GET_USER_DATA_REQUEST';
const GET_USER_DATA_SUCCESS = 'react-redux-gallery/auth/GET_USER_DATA_SUCCESS';
const GET_USER_DATA_FAILURE = 'react-redux-gallery/auth/ GET_USER_DATA_FAILURE';

const initialState = {
	userData: {
		userId: '',
		username: ''
	},
	loading: false,
	err: null
};

export default function reducer(state = initialState, action) {
	switch (action.type) {
		case SIGNUP_REQUEST: {
			return { ...state, loading: true };
		}
		case SIGNUP_SUCCESS: {
			return { ...state, loading: false, err: null };
		}
		case SIGNUP_FAILURE: {
			return { ...state, loading: false, err: action.payload };
		}
		case LOGIN_REQUEST: {
			return { ...state, loading: true };
		}
		case LOGIN_SUCCESS: {
			return { userData: action.payload, loading: false, err: null };
		}
		case LOGIN_FAILURE: {
			return { ...state, loading: false, err: action.payload };
		}
		case GET_USER_DATA_REQUEST: {
			return { ...state, loading: true };
		}
		case GET_USER_DATA_SUCCESS: {
			return { ...state, userData: action.payload, loading: false };
		}
		case GET_USER_DATA_FAILURE: {
			return { ...state, loading: false };
		}
		default: {
			return state;
		}
	}
}

export const signupRequest = () => {
	return {
		type: SIGNUP_REQUEST
	};
};

export const signupSuccess = (user) => {
	return {
		type: SIGNUP_SUCCESS,
		payload: user
	};
};

export const signupFailure = (err) => {
	return {
		type: SIGNUP_FAILURE,
		payload: err
	};
};

export const loginRequest = () => {
	return {
		type: LOGIN_REQUEST
	};
};

export const loginSuccess = (userData) => {
	return {
		type: LOGIN_SUCCESS,
		payload: userData
	};
};

export const loginFailure = (err) => {
	return {
		type: LOGIN_FAILURE,
		payload: err
	};
};

export const getUserDataRequest = () => {
	return {
		type: GET_USER_DATA_REQUEST
	};
};

export const getUserDataSuccess = (userData) => {
	return {
		type: GET_USER_DATA_SUCCESS,
		payload: userData
	};
};

export const getUserDataFailure = () => {
	return {
		type: GET_USER_DATA_FAILURE
	};
};

export const getUserData = () => {
	return (dispatch) => {
		dispatch(getUserDataRequest());
		return new Promise((resolve) => {
			axios
				.get('http://localhost:3000/userdata', {
					headers: {
						Authorization: `Bearer ${localStorage.getItem('token')}`
					}
				})
				.then((res) => {
					const userData = res.data;
					dispatch(getUserDataSuccess(userData));
					resolve();
				})
				.catch(() => {
					dispatch(getUserDataFailure());
				});
		});
	};
};

export const signUp = (values) => {
	return (dispatch) => {
		return new Promise((resolve) => {
			dispatch(signupRequest());

			axios
				.post('http://localhost:3000/signup', values)
				.then(() => {
					dispatch(signupSuccess());
					resolve();
				})
				.catch((err) => {
					const message = err.response.data;
					dispatch(signupFailure(message));
				});
		});
	};
};

export const logIn = (values) => {
	return (dispatch) => {
		return new Promise((resolve) => {
			dispatch(loginRequest());

			axios
				.post('http://localhost:3000/login', values)
				.then((res) => {
					const token = res.data.token;
					const userData = res.data.userData;

					localStorage.setItem('token', token);
					dispatch(loginSuccess(userData));
					resolve();
				})
				.catch((err) => {
					if (err.response.code === 401) {
						dispatch(loginFailure('Wrong email or password'));
					} else {
						console.log(err.response.code);
						dispatch(loginFailure('Something went wrong'));
					}
				});
		});
	};
};
