import axios from 'axios';

const SIGNUP_SUCCESS = 'react-redux-gallery/auth/SIGNUP_SUCCESS';
const LOGIN_SUCCESS = 'react-redux-gallery/auth/LOGIN_SUCCESS';

const initialState = {
	user: {
		email: ''
	}
};

export default function reducer(state = initialState, action) {
	switch (action.type) {
		case SIGNUP_SUCCESS: {
			return { ...state, user: action.payload };
		}
		default: {
			return state;
		}
	}
}

export const signUpSuccess = (user) => {
	return {
		type: SIGNUP_SUCCESS,
		payload: user
	};
};

export const signUp = (values) => {
	return (dispatch) => {
		axios.post('http://localhost:3000/signup', { values }).then((res) => {
			const user = res.data.user;
			dispatch(signUpSuccess(user));
		});
	};
};

export const logIn = (values) => {
	return (dispatch) => {
		axios.post('http://localhost:3000/login', { values }).then((res) => {
			const user = res.data.user;
			dispatch(logInSuccess(user));
		});
	};
};
