export const SIGN_UP = 'auth/SIGN_UP';

const initialState = {
	user: null
};

export default (state = initialState, action) => {
	switch (action.type) {
		case 'SIGN_UP':
			return {
				...state,
				user: action.payload
			};
	}
};

export const signUp = () => ({
	type: SIGN_UP
});
