export const SIGN_UP = 'auth/SIGN_UP';

const initialState = {
	user: null
};

export default function reducer(state = initialState, action) {
	switch (action.type) {
		case 'SIGN_UP':
			return {
				...state,
				user: action.payload
			};
		default:
			return state;
	}
}

export const signUp = () => ({
	type: SIGN_UP
});
