const SIGN_UP = 'SIGN_UP';

const initialState = {
	user: {
		email: ''
	}
};

export default (state = initialState, action) => {
	switch (action.type) {
		case SIGN_UP: {
			return { ...state, user: action.payload.user };
		}
		default: {
			return state;
		}
	}
};

export const signUp = () => ({
	type: SIGN_UP
});
