import { SIGNUP_SUCCESS } from './authTypes';

const initialState = {
	user: {
		email: 'Hello World'
	}
};

const authReducer = (state = initialState, action) => {
	switch (action.type) {
		case SIGNUP_SUCCESS:
			return {
				...state,
				user: action.payload
			};
		default:
			return state;
	}
};

export default authReducer;
