import axios from 'axios';

const SIGNUP_REQUEST = 'react-redux-gallery/auth/SIGNUP_REQUEST';
const SIGNUP_SUCCESS = 'react-redux-gallery/auth/SIGNUP_SUCCESS';
const SIGNUP_FAILURE = 'react-redux-gallery/auth/SIGNUP_FAILURE';

const LOGIN_REQUEST = 'react-redux-gallery/auth/LOGIN_REQUEST';
const LOGIN_SUCCESS = 'react-redux-gallery/auth/LOGIN_SUCCESS';
const LOGIN_FAILURE = 'react-redux-gallery/auth/LOGIN_FAILURE';

const initialState = {
	userData: {
		userId: '',
		username: ''
	},
	loading: false,
	err: null
};

export default function reducer(state = initialState, action) {
	switch (action.type) {
		case SIGNUP_REQUEST: {
			return { ...state, loading: true };
		}
		case SIGNUP_SUCCESS: {
			return { ...state, loading: false, err: null };
		}
		case SIGNUP_FAILURE: {
			return { ...state, loading: false, err: action.payload };
		}
		case LOGIN_REQUEST: {
			return { ...state, loading: true };
		}
		case LOGIN_SUCCESS: {
			return { userData: action.payload, loading: false, err: null };
		}
		case LOGIN_FAILURE: {
			return { ...state, loading: false, err: action.payload };
		}
		default: {
			return state;
		}
	}
}

export const signupRequest = () => {
	return {
		type: SIGNUP_REQUEST
	};
};

export const signupSuccess = (user) => {
	return {
		type: SIGNUP_SUCCESS,
		payload: user
	};
};

export const signupFailure = (err) => {
	return {
		type: SIGNUP_FAILURE,
		payload: err
	};
};

export const loginRequest = () => {
	return {
		type: LOGIN_REQUEST
	};
};

export const loginSuccess = (userData) => {
	return {
		type: LOGIN_SUCCESS,
		payload: userData
	};
};

export const loginFailure = (err) => {
	return {
		type: LOGIN_FAILURE,
		payload: err
	};
};

export const getUserData = () => {
	return (dispatch) => {
		return new Promise((resolve, reject) => {
			axios
				.post('http://localhost:3000/userdata', {
					headers: {
						Authorization: 'Bearer' + localStorage.getItem('token')
					}
				})
				.then(() => {});
		});
	};
};

export const signUp = (values) => {
	return (dispatch) => {
		return new Promise((resolve, reject) => {
			dispatch(signupRequest());

			axios
				.post('http://localhost:3000/signup', values)
				.then(() => {
					dispatch(signupSuccess());

					resolve();
				})
				.catch((err) => {
					const message = err.response.data;
					dispatch(signupFailure(message));

					reject();
				});
		});
	};
};

export const logIn = (values) => {
	return (dispatch) => {
		return new Promise((resolve, reject) => {
			dispatch(loginRequest());

			axios
				.post('http://localhost:3000/login', values)
				.then((res) => {
					const token = res.data.token;
					const userData = res.data.userData;

					localStorage.setItem('token', token);
					localStorage.setItem('userData', JSON.stringify(userData));
					dispatch(loginSuccess(userData));

					resolve();
				})
				.catch((err) => {
					const message = err.response.data;
					dispatch(loginFailure(message));

					reject();
				});
		});
	};
};
