import { SIGN_UP } from './authTypes';

const initialState = {
	user: { msg: 'Hello World' }
};

const authReducer = (state = initialState, action) => {
	switch (action.type) {
		case SIGN_UP:
			return {
				...state,
				user: action.payload.user
			};
		default: {
			return {
				...state
			};
		}
	}
};

export default authReducer;
